function Validate() {
    var page = document.getElementById("ddlpages");
    var inp = document.getElementById('fileToUpload');
    if (inp.files.length == 0) {
        alert("Please select File!");
        inp.focus();
        return false;
    }
    else if (page.value == "") {
        alert("Please select Page!");
        page.focus();
        return false;
    }
    return true;
}

function ValidateZone() {    
    var zone = document.getElementById("WidgetZone");
    if (zone.value == "") {
        alert("Please Enter widget zone with out space.");
        zone.focus();
        return false;
    }
    return true;
}



